#!/usr/bin/env python# Exercise: Presidents  # Write a program to:# (1) Load the data from presidents.txt into a dictionary.# (2) Print the years the greatest and least number of presidents were alive.#     (between 1732 and 2015 (inclusive))#     Ex. #       'least = 2015'#       'most = 2015'# Bonus: Confirm there are no ties.############################################################################### Importsdef presidents():    president_dict = {}    president_year={}    with open("presidents.txt") as f:        text = f.read().splitlines()        for item in text:            split = item.split(',')            president_dict[split[0]] = [split[1],split[2]]        for i in range(1732,2015):        	for key, item in president_dict:        		if item[2]==None:        			item[2]=='2015'        		if i in range(item[1],item[2]):        			president_year[i]=president_year.get(i,0)+1           #            #     while split[2]!='None':           #         if i in range(int(split[1]),int(split[2])):           #             d[i]=d.get(i,0)+1           # print d									# Body##############################################################################def main():  # CALL YOUR FUNCTION BELOW	presidents()if __name__ == '__main__':    main()